mapper.select:
SELECT * FROM (SELECT * FROM ADOPT_POST_TBL INNER JOIN ANIMAL_TBL USING(ANIMAL_NO) ORDER BY ANIMAL_NO DESC)
  
    
    	
	      AND ANIMAL_SPECIES = #{animalSpecies }
    	
    
    
      AND ANIMAL_ADDR = #{animalAddr }
    
    
      AND ADOPT_CREATE_DATE >= #{adoptCreateDateStart,jdbcType=TIMESTAMP}
    
    
      AND ADOPT_CREATE_DATE <= #{adoptCreateDateEnd,jdbcType=TIMESTAMP}
